import React, { useState, useEffect } from "react";
import { Route, Switch } from "react-router-dom";
import axios from 'axios';
import Home from './components/Home';
import Form from './components/Form';
import Recipe from "./components/Recipe";
import Recipes from "./components/RecipeList";

const App = () => {

  const key = '37f23969e9634c3fde3c44a641daf99c';
  const apiId = '37885762';

  const [recipes, setRecipes] = useState([])

  const getRecipe = async (e) => {
    e.preventDefault();
    const recipeName = e.target.elements.recipeName.value;
    //console.log(recipeName);
    useEffect(() => {
      axios.get(`https://api.edamam.com/search?q=chicken&app_id=${apiId}&app_key=${key}`)
        .then((res) => {
          setRecipes(res.data)
          console.log(recipes)
        })
        .catch((err) => {
          console.log(err)
        })

    },[])
    //just making api calls
    const api = await fetch(`https://api.edamam.com/search?q=${recipeName}&app_id=${apiId}&app_key=${key}`);
    //grabbing actual api data
    const data = await api.json() //parse our api data into json and store into our data variable
    setRecipes(data.hits);
    console.log(recipes);
  }



  return (
    <div>
      <h1>Recipe Search</h1>
      <Home/>
      <Form
        getRecipe={recipes}
      />
      {/* {recipes.map(item => {
        return (
          <Recipes 
            image={item.recipe.image}
            name={item.recipe.label}
            view='View Recipe'
            />
        )
      })} */}
      <div>
        <Switch>
          <Route exact path='/' component={Home}/>
          <Route path='/recipe/:id'>
            <Recipe food={recipes}/>
          </Route>
        </Switch>
      </div>
    </div>
  );
};

export default App;
