import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import { GiHamburgerMenu } from 'react-icons/gi';
import { GrCart } from 'react-icons/gr';
import styled from 'styled-components';
import breakpoint from '../../breakpoint';

const Navigation = ({cart}) => {
    const [showMenu, setShowMenu] = useState(false);


    return (
        <Wrapper>
            <div className='mobile-nav'>
                <div className='nav-items'>
                    <span onClick={''}><GiHamburgerMenu size={35}/></span>
                    <MenuLink to='/'><p>Home</p></MenuLink>
                    <MenuLink to='/shop'><p>New Releases</p></MenuLink>
                    <MenuLink to='/shop'><p>Men</p></MenuLink>
                    <MenuLink to='/shop'><p>Women</p></MenuLink>
                    <MenuLink to='/shop'><p>All Shoes</p></MenuLink>
                    </div>
                    <div className='menu__btns'>
                    {cart.length > 0 ? 
                        <div>
                        <MenuLink to='/cart'><GrCart size={30}/></MenuLink>
                        <Link to='/cart'>
                            <div className='cart-notifications'>
                            <span className='cart-item-num'>{cart.length}</span>
                            </div>
                        </Link>
                        </div>
                    :
                        <MenuLink to='/cart'><GrCart size={30}/></MenuLink>
                    }
                    <MenuLink to='/membership'><button className='nav-btn'>Join us</button></MenuLink>
                    <MenuLink to='/sign-in'><button className='nav-btn'>Sign in</button></MenuLink> 
                </div>
            </div>
          </Wrapper>
    )
}

export default Navigation;

const Wrapper = styled.nav`
.mobile-nav {
    display: flex;
    //display: none;
    background-color: yellow;
    justify-content: space-between;
  }

//   .nav-items {
//     display: flex;
//   }

  .menu__btns {
    display: flex;
    position: relative;
  }
 .nav-header {
   display: flex;
   justify-content: space-between;
 }
 .cart-notifications {
    position: absolute;
    left: 80px;
    bottom: 40px;
    background-color: crimson;
    color: white;
    border-radius: 50%;
    text-align: center;
    height: 25px;
    width: 25px;

  }

  .cart-item-num {
    font-size: 12px;
  }

  @media only screen and ${breakpoint.device.desktop} {
    .mobile-nav {
        background-color: white;
      }
      .nav-header {
        display: flex;
        justify-content:
      }
}
`
const MenuLink = styled(Link)`
    text-decoration: none;
    color: #131921;
    padding: 15px;
    text-transform: uppercase;
    font-weight: 700;
    font-size: 15px;

    @media only screen and ${breakpoint.device.desktop} {


    }
  `
