import React from 'react';
import { useParams } from "react-router-dom";
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import breakpoint from '../../breakpoint';

//comp
import Footer from './Footer';

//images
import landingHero from '../../images/landing-hero.png';
import heroRight from '../../images/desktop-bg-hero.png';
import one from '../../images/landing-one.png';
import two from '../../images/landing-two.png';
import three from '../../images/products/im.png';
import four from '../../images/products/fourteen-new.png';
import hero from '../../images/hero.png';
import logo from '../../images/logo.png';

const NewHome = ({data}) => {

    document.title = 'LacedUp | Stylish Sneakers For Less';

    const params = useParams();

    const shoe = data.find(item => `${item.id}` === params.id);
    console.log("heroes", shoe)

    const firstSection = [
        {
        name: '',
        image: one,
        description: 'Shoes Always $100 & Under',
        cta: 'Shop',
        link: '/shop',
        id: 1
        },
        {
        name: '',
        image: two,
        description: `Shrug off grey winter days. Shop spring styles.`,
        cta: 'Shop',
        link: '/shop',
        id: 2
        },
    ]
    return (
        <section>
            <div style={{margin: '0 60px'}}>


            <div style={{position: 'relative'}}>
                <img src={landingHero} alt='woman resting on rock' style={{width: '100%', height: '50%'}}/>
                <div className='hero-left' style={{position: 'absolute', top: '50px'}}>
                    <h1 className='heading-small' style={{ textAlign: 'left', fontFamily: `'Oswald', sans-serif`, textTransform: 'uppercase', margin: '0', color: 'white'}}>new year,<br/>new energy.</h1>
                    <h1 className='heading-big' style={{ textAlign: 'left', fontFamily: `'Oswald', sans-serif`, textTransform: 'uppercase', margin: '0', color: 'white'}}>new <br/>year,<br/>new <br/>energy.</h1>
                    <p style={{color: 'white'}}>Take on anything the new year throws your way in<br/>these functional styles.</p>
                    <Link to='/shop'><button className='hero-btn'>Shop</button></Link>
                    <button>Explore</button>
                </div>
            </div>

            <div>
            <h2>Trending Now</h2>
            <div style={{display: 'flex'}}>

                {firstSection.map(item => (
                    <div style={{position: 'relative'}}>
                        <img src={item.image} alt={item.name} style={{width: '100%'}}/>
                        <p style={{position: 'absolute', bottom: '10px', color: 'white', paddingLeft: '20px;'}}>{item.description}</p>
                    {/* // <div style={{display: 'flex', justifyContent: 'space-around'}}>
                    // <div style={{width: '50%'}}>
                    //     <img src={item.image} style={{width: '100%'}}/>
                    // </div>
                    // <div style={{width: '50%'}}>
                    // <img src={two} style={{width: '100%'}}/>
                    // </div> */}
                </div>
                ))}


            </div>
            </div>


            <div style={{display: 'flex', overflowY: 'hidden', overflowX: 'auto', whiteSpace: 'nowrap'}}>
              {data.map(item => {
                return (
                  <div key={item.id}>
                    <div>
                    <Link to={`/shoe/${item.id}`}><img src={item.image} alt={item.name} style={{width: '300px', marginRight: '7px'}}/></Link>
                      <div style={{textAlign: 'left'}}>
                        <p style={{margin: '0', paddingTop: '5px', fontWeight: '700'}}>{item.name}</p>
                        <p style={{margin: '0', color: '#767676'}}>{item.subTitle}</p>
                        <p style={{margin: '0', color: '#767676'}}>{item.availability}</p>
                        <p style={{margin: '0', paddingTop: '5px', fontWeight: '700'}}>${item.price}</p>
                      </div>
                    </div>
                  </div>
                )
              })}
            </div>

            <div>
                hero section
            </div>

            <div style={{display: 'flex', justifyContent: 'space-around'}}>
                <div>
                    one
                </div>
                <div>
                    two
                </div>
                <div>
                    three
                </div>
            </div>
            </div>
        <Footer/>
        </section>
    )
}

export default NewHome;
