import React, { useState, useEffect } from "react";
import {Switch, Link, Route} from 'react-router-dom';
import styled from 'styled-components';
import breakpoint from './breakpoint';
import { data } from './data';
import Cart from './components/Cart';
import Home from './components/Home';
import Shop from './components/Shop';
import './App.css';

const App = () => {
  const [cart, setCart] = useState([]);

  //adds users selected item
  const addToCart = (product, qty) => {
    const item = {product, qty}
    setCart([...cart, item]);
    console.log(`${product} has been added to cart.`)    
  }
  console.log('Selected Items:', cart)

  //totals up item costs
  const totalPrice = cart.reduce((prev, curr) => {
    return prev + curr.product.price
  }, 0);
  console.log('total price: $',totalPrice)



  return (

      <Wrapper className='App'>
        <header>
          <nav>
            <div className='nav-center'>
              <div className='nav-header'>
                {/* <Link to='/'><img src={logo} alt='Company logo' className='logo'/></Link>

                  <div className='toggle-icon' onClick={toggleMenu}>
                    <GiHamburgerMenu size={30}/>
                  </div> */}
              </div>
              <div className='nav-links desktop-links'>
                <div className='nav-items'>
                  <MenuLink to='/'><p>Home</p></MenuLink>
                  <MenuLink to='/new-releases'><p>New Releases</p></MenuLink>
                  <MenuLink to='/mens-shoes'><p>Men</p></MenuLink>
                  <MenuLink to='/womens-shoes'><p>Women</p></MenuLink>
                  <MenuLink to='/shop'><p>All Shoes</p></MenuLink>
                </div>
                <div className='menu__btns'>
                  <MenuLink to='/membership'><button className='nav-btn'>Join us</button></MenuLink>
                  <MenuLink to='/sign-in'><button className='nav-btn'>Sign in</button></MenuLink>
                  <MenuLink to='/cart'><button className='nav-btn'>Cart</button></MenuLink>

                </div>
              </div>
            </div>
          </nav>
        </header>
        <Switch>
          <Route exact path='/' component={Home}/>
          <Route exact path='/shop'>
            <Shop data={data} addToCart={addToCart}/>
          </Route>
            <Route exact path='/cart'>
              <Cart length={cart.length} cartItems={cart} totalPrice={totalPrice}/>
            </Route>
        </Switch>

      </Wrapper>




  );
};

export default App;


const Wrapper = styled.div`

  .width-container {
    //max-width: 1920px;
    //margin: 0 auto;
  }

    .logo {
      width: 50px;
      display: block;
      margin-left: auto;
      margin-right: auto;
    }

   .nav-header {
     display: flex;
     justify-content: space-between;
   }
   .desktop-links {
     display: none;
   }

   .show-links {
    display: block;
   }


    @media only screen and ${breakpoint.device.desktop} {
     //background-color: red;

      .nav-header {
        display: flex;
        justify-content:
      }

      .logo {
        width: 50px;
        display: block;
        margin-left: auto;
        margin-right: auto;
      }

      .nav-links {
        //display:none;
      }

      .nav-center {
        display: flex;
        justify-content: space-between;
      }

      .desktop-links {
        display: block;
        display: flex;

      }

      .nav-items {
        display: flex;

      }

      .toggle-icon {
        display: none;
      }

      .menu__btns {
        display: flex;
        align-items: center;
        justify-content: center;
        padding-left: 50px;
      }

      .menu__btns button {
        cursor: pointer;
      }
    }
  `;

  const MenuLink = styled(Link)`
    text-decoration: none;
    color: white;
    padding: 15px;
    text-transform: uppercase;
    font-weight: 700;
