import React, { useState, useEffect } from "react";
import {Switch, Link, Route} from 'react-router-dom';
import { GiHamburgerMenu } from 'react-icons/gi';
import styled from 'styled-components';
import breakpoint from './breakpoint';
import { data } from './data';
import Cart from './client/components/Cart';
import Home from './client/components/Home';
import Shop from './client/components/Shop';
import Shoe from './client/components/Shoe';
import Login from './client/user/Login';
import Register from './client/user/Register';
import logo from './images/logo.png';
import './App.css';

//icons
import { GrCart } from 'react-icons/gr';
import Navigation from "./client/components/Navigation";
const App = () => {
  const [cart, setCart] = useState([]);
  const [mobileMenu, setMobileMenu] = useState();

  useEffect(() => {

    const setHamburgerMenu = () => {

    }
    setHamburgerMenu();

  },[])

  //adds users selected item
  const addToCart = (product, qty) => {
    const item = {product, qty}
    setCart([...cart, item]);
    console.log(`${product} has been added to cart.`)    
  }
  console.log('Selected Items:', cart)

  //totals up item costs
  const totalPrice = cart.reduce((prev, curr) => {
    return prev + curr.product.price
  }, 0);
  console.log('total price: $',totalPrice)



  return (

      <Wrapper className='App'>
        <header>
          <img src={logo} alt='logo' className='logo'/>
          <Navigation cart={cart}/>
        </header>

        <Switch>
          <Route exact path='/'>
            <Home data={data}/>
          </Route>
          <Route exact path='/shoe/:id'>
            <Shoe data={data} addToCart={addToCart} cartItems={cart}/>
          </Route>
          <Route exact path='/shop'>
            <Shop data={data} addToCart={addToCart}/>
          </Route>
          <Route exact path='/cart'>
            <Cart length={cart.length} cartItems={cart} totalPrice={totalPrice}/>
          </Route>
          <Route exact path='/sign-in' component={Login}/>
          <Route exact path='/membership' component={Register}/>
        </Switch>

      </Wrapper>




  );
};

export default App;


const Wrapper = styled.div`

  .width-container {
    //max-width: 1920px;
    //margin: 0 auto;
  }

    .logo {
      width: 50px;
      height: 50px;
      display: block;
      margin-left: auto;
      margin-right: auto;
    }


   button {
    background-color: #111;
    color: white;
    border: none;
    border-radius: 25px;
    padding: 10px 20px;
  }

  button:hover {
    pointer: cursor;
  }


    @media only screen and ${breakpoint.device.desktop} {
     //background-color: red;

      nav {
        background-color: white;
      }
      .nav-header {
        display: flex;
        justify-content:
      }

