        margin-right: auto;
      }

      .nav-links {
        //display:none;
      }

      .nav-center {
        display: flex;
        justify-content: space-between;
      }

      .nav-items {
        display: flex;

      }

      .toggle-icon {
        display: none;
      }

      .menu__btns {
        display: flex;
        align-items: center;
        justify-content: center;
        padding-left: 50px;
      }

      .menu__btns button {
        cursor: pointer;
      }

      .cart-notifications {
