import React, { useState, useEffect } from "react";
import {Switch, Link, Route} from 'react-router-dom';
import CartTracker from './components/CartTracker';
import Home from './components/Home';
import Shop from './components/Shop';
import './App.css';

const App = () => {
  const [cart, setCart] = useState([]);

  const data = [
    {
      id: 1,
      name: 'Blanket',
      cost: 19.99,
      image: 'https://www.burtsbeesbaby.com/media/catalog/product/H/M/HM10323_Heather%20Grey_alt1.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=90&width=90&canvas=90:90'
    },
    {
      id: 2,
      name: 'Stuffed Bear',
      cost: 19.99,
      image: 'https://i5.walmartimages.com/asr/f10661d8-1568-442a-9a52-36f8fa085084.4d90194419636204495c955bb374e632.jpeg'
    },
    {
      id: 3,
      name: 'Highchair',
      cost: 19.99,
      image: 'https://assets.costway.com/media/catalog/product/cache/1/image/530x/9df78eab33525d08d6e5fb8d27136e95/1/_/1_359_58.jpg'
    },
    {
      id: 4,
      name: 'Blanket',
      cost: 19.99,
      image: 'https://www.burtsbeesbaby.com/media/catalog/product/H/M/HM10323_Heather%20Grey_alt1.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=90&width=90&canvas=90:90'
    },
    {
      id: 5,
      name: 'Stuffed Bear',
      cost: 19.99,
      image: 'https://i5.walmartimages.com/asr/f10661d8-1568-442a-9a52-36f8fa085084.4d90194419636204495c955bb374e632.jpeg'
    },
    {
      id: 6,
      name: 'Highchair',
      cost: 19.99,
      image: 'https://assets.costway.com/media/catalog/product/cache/1/image/530x/9df78eab33525d08d6e5fb8d27136e95/1/_/1_359_58.jpg'
    } 
  ]

  const addToCart = (product) => {
    console.log('in add to cart')
    setCart([...cart, product]);
  }

  return (

      <div className='App'>
       <header>
         <nav>
           <Link to='/'>Home</Link>
           <Link to='/shop'>Shop</Link>
           <Link to='/cart'>Cart ({cart.length})</Link>
         </nav>
       </header>

        <Switch>
          <Route exact path='/' component={Home}/>
          <Route exact path='/shop'>
            <Shop data={data}/>
          </Route>
            <Route exact path='/item-tracker'>
              <CartTracker length={cart.length} addToCart={addToCart}/>
            </Route>
        </Switch>
      </div>


  );
};

export default App;
