import React, { useState, useEffect } from "react";
import { Route, Switch, useParams } from "react-router-dom";
import axios from 'axios';
import Recipe from "./components/Recipe";
import RecipeList from "./components/RecipeList";

function Home() {

    const key = '37f23969e9634c3fde3c44a641daf99c';
  const apiId = '37885762';

  const [data, setData] = useState({hits: [] })
  const [query, setQuery] = useState('lobster')

  // This effect will fire when the component mounts, AND each time "query" is updated!
  useEffect(() => {

    const getRecipe = () => {
      axios.get(`https://api.edamam.com/search?q=${query}&app_id=${apiId}&app_key=${key}`)
      .then((res) => {
        setData(res.data)
        console.log(data)
      })
      .catch((err) => {
        console.log(err)
      })
    }

    getRecipe();

  }, [query]) // Effect sybced with query; when query changes, fire this effect again

  const changeHandler = e => {
    setQuery(e.target.value)
  }
  return (
<div>
      <h1>Recipe Search</h1>
        <input
            value={query}
            onChange={changeHandler}
          />

      {data.hits.map(item => {
        return (
          <RecipeList 
            image={item.recipe.image}
            name={item.recipe.label}
            view='View Recipe'
            id={item.recipe.totalWeight}
            />
        )
      })}
    </div>
  )
}

export default Home;
