import React, { useState, useEffect } from "react";
import {Switch, Link, Route} from 'react-router-dom';
import Cart from './components/Cart';
import './App.css';

const App = () => {
  const [cart, setCart] = useState([]);

  const data = [
    {
      id: 1,
      name: 'Blanket',
      cost: 19.99,
      image: 'https://www.burtsbeesbaby.com/media/catalog/product/H/M/HM10323_Heather%20Grey_alt1.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=90&width=90&canvas=90:90'
    },
    {
      id: 2,
      name: 'Stuffed Bear',
      cost: 19.99,
      image: 'https://i5.walmartimages.com/asr/f10661d8-1568-442a-9a52-36f8fa085084.4d90194419636204495c955bb374e632.jpeg'
    },
    {
      id: 3,
      name: 'Highchair',
      cost: 19.99,
      image: 'https://assets.costway.com/media/catalog/product/cache/1/image/530x/9df78eab33525d08d6e5fb8d27136e95/1/_/1_359_58.jpg'
    },
    {
      id: 4,
      name: 'Blanket',
      cost: 19.99,
      image: 'https://www.burtsbeesbaby.com/media/catalog/product/H/M/HM10323_Heather%20Grey_alt1.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=90&width=90&canvas=90:90'
    },
    {
      id: 5,
      name: 'Stuffed Bear',
      cost: 19.99,
      image: 'https://i5.walmartimages.com/asr/f10661d8-1568-442a-9a52-36f8fa085084.4d90194419636204495c955bb374e632.jpeg'
    },
    {
      id: 6,
      name: 'Highchair',
      cost: 19.99,
      image: 'https://assets.costway.com/media/catalog/product/cache/1/image/530x/9df78eab33525d08d6e5fb8d27136e95/1/_/1_359_58.jpg'
    } 
  ]

  const addToCart = (product) => {
    console.log('in add to cart')
    setCart([...cart, product]);
  }

  return (

      <div className='App'>
        <header>
          <Link to=''>
            <button>
              <Cart/>
            </button>
          </Link>

        </header>
        <h1>Products</h1>
        <div className='products'>
          {data.map((item, index) => (
            <div key={index} className='product-item'>
              <h3>{item.name}</h3>
              <h4>${item.cost}</h4>
              <img src={item.image} alt={item.name}/>
              <br/>
              <button onClick={() => addToCart()}>Add to Cart</button>
            </div>
          ))}
        </div>

        <Switch>
            <Route>
              <Cart/>
            </Route>
        </Switch>
      </div>


  );
};

export default App;
