import React, { useState, useEffect } from "react";
import { Route, Switch } from "react-router-dom";
import axios from 'axios';
import Home from './components/Home';
// import Form from './components/Form';
import Recipe from "./components/Recipe";
import Recipes from "./components/RecipeList";

const App = () => {

  const key = '37f23969e9634c3fde3c44a641daf99c';
  const apiId = '37885762';

  const [recipes, setRecipes] = useState({hits: [] })
  const [query, setQuery] = useState('lobster')

  useEffect(() => {

    const getRecipe = () => {
      axios.get(`https://api.edamam.com/search?q=${query}&app_id=${apiId}&app_key=${key}`)
      .then((res) => {
        setRecipes(res.data)
        console.log(recipes)
      })
      .catch((err) => {
        console.log(err)
      })
    }

    getRecipe();

  },[query])

  const submitHandler = e => {
    e.preventDefault()

  }

  return (
    <div>
      <h1>Recipe Search</h1>
      <form onSubmit={submitHandler}>
        <input
            type='text'
            name='recipeName'
            // value={query}
            // onChange={e => setQuery(e.target.value)}
          />
          <button type='submit'>Search</button>
      </form>



      {/* {recipes.map(item => {
        return (
          <Recipes 
            image={item.recipe.image}
            name={item.recipe.label}
            view='View Recipe'
            />
        )
      })} */}
      <div>
        <Switch>
          <Route exact path='/' component={Home}/>
          <Route path='/recipe/:id'>
            <Recipe food={recipes}/>
          </Route>
        </Switch>
      </div>
    </div>
  );
};

export default App;
