import React, { useState, useEffect } from "react";
import {Switch, Link, Route} from 'react-router-dom';
import { data } from './data';
import Cart from './components/Cart';
import Home from './components/Home';
import Shop from './components/Shop';
import './App.css';

const App = () => {
  const [cart, setCart] = useState([]);



  //adds users selected item
  const addToCart = (product, qty) => {
    const item = {product, qty}
    setCart([...cart, item]);
    console.log(`${product} has been added to cart.`)    
  }
  console.log('Selected Items:', cart)

  //totals up item costs
  const totalPrice = cart.reduce((prev, curr) => {
    return prev + curr.product.price
  }, 0);
  console.log('total price: $',totalPrice)



  return (

      <div className='App'>
       <header>
         <nav>
           <Link to='/' className='nav-item' style={{textDecoration: 'none'}}>Home</Link>
           <Link to='/shop' className='nav-item' style={{textDecoration: 'none'}}>Shop</Link>
           <Link to='/cart' className='nav-item' style={{textDecoration: 'none'}}>Cart<span className='notification'>{cart.length}</span></Link>
         </nav>
       </header>

        <Switch>
          <Route exact path='/' component={Home}/>
          <Route exact path='/shop'>
            <Shop data={data} addToCart={addToCart}/>
          </Route>
            <Route exact path='/cart'>
              <Cart length={cart.length} cartItems={cart} totalPrice={totalPrice}/>
            </Route>
        </Switch>
      </div>


  );
};

export default App;
