import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import { GiHamburgerMenu } from 'react-icons/gi';
import { GrCart } from 'react-icons/gr';
import styled from 'styled-components';
import breakpoint from '../../breakpoint';

const Navigation = ({cart}) => {
    const [showMenu, setShowMenu] = useState(false);

    let menu

    if(showMenu) {
        menu = 
        <div className='mobile-nav-items'>
            <div>
                <MenuLink to='/'><p>Home</p></MenuLink>
                <MenuLink to='/shop'><p>New Releases</p></MenuLink>
                <MenuLink to='/shop'><p>Men</p></MenuLink>
                <MenuLink to='/shop'><p>Women</p></MenuLink>
                <MenuLink to='/shop'><p>All Shoes</p></MenuLink>
            </div>
                <div>
                {cart.length > 0 ?
                <div>
                <MenuLink to='/cart'><GrCart size={30}/></MenuLink>
                <Link to='/cart'>
                    <div className='cart-notifications'>
                    <span className='cart-item-num'>{cart.length}</span>
                    </div>
                </Link>
                </div>
                    :
                <MenuLink to='/cart'><GrCart size={30}/></MenuLink>
                }
                <MenuLink to='/membership'><button className='nav-btn'>Join us</button></MenuLink>
                <MenuLink to='/sign-in'><button className='nav-btn'>Sign in</button></MenuLink> 
            </div>
        </div>

    }

    return (
        <Wrapper>
            <div className='mobile-nav'>
                <span>
                    <GiHamburgerMenu 
                        size={40}
                        onClick={() => setShowMenu(!showMenu)} //toggles menu open and closed
                    />
                </span>
                {menu}
            </div>
            <div className='desktop-nav'>
                <div className='menu-left'>
                <MenuLink to='/'><p>Home</p></MenuLink>
                <MenuLink to='/shop'><p>New Releases</p></MenuLink>
                <MenuLink to='/shop'><p>Men</p></MenuLink>
                <MenuLink to='/shop'><p>Women</p></MenuLink>
                <MenuLink to='/shop'><p>All Shoes</p></MenuLink>
                </div>
                <div className='menu__btns'>
                    {cart.length > 0 ? 
                <div>
                <MenuLink to='/cart'><GrCart size={30}/></MenuLink>
                <Link to='/cart'>
                    <div className='cart-notifications'>
                    <span className='cart-item-num'>{cart.length}</span>
                    </div>
                </Link>
                </div>
                    :
                <MenuLink to='/cart'><GrCart size={30}/></MenuLink>
                }
                <MenuLink to='/membership'><button className='nav-btn'>Join us</button></MenuLink>
                <MenuLink to='/sign-in'><button className='nav-btn'>Sign in</button></MenuLink> 

                </div>
            </div>
          </Wrapper>
    )
}

export default Navigation;

const Wrapper = styled.nav`
.mobile-nav {
    display: flex;
    //display: none;

    justify-content: space-between;
    position: relative;
    z-index: 2;
  }

  .desktop-nav {
      display: none;
  }

  .mobile-nav-items {
    background-color: yellow;
   position: absolute;
   //margin-top: 20px;
   top: 10;
   left: 0;
   z-index: 1;
  }

p {
    margin: 0;
}
  .menu__btns {
    display: flex;
    position: relative;
  }
 .nav-header {
   display: flex;
   justify-content: space-between;
 }
 .cart-notifications {
    position: absolute;
    left: 80px;
    bottom: 40px;
    background-color: crimson;
    color: white;
    border-radius: 50%;
    text-align: center;
    height: 25px;
    width: 25px;

  }

  .cart-item-num {
    font-size: 12px;
  }

  @media only screen and ${breakpoint.device.desktop} {

      .mobile-nav {
          display: none;
      }

    .desktop-nav {
        display: block;
        display: flex;
    }
    .menu-left {
        display: flex;
    }
      .nav-header {
        display: flex;
        justify-content:
      }
}
`
const MenuLink = styled(Link)`
    text-decoration: none;
    color: #131921;
    padding: 15px;
    text-transform: uppercase;
    font-weight: 700;
    font-size: 15px;

    @media only screen and ${breakpoint.device.desktop} {


    }
  `
