            </div>
        </div>

    }

    return (
        <Wrapper>
            <div className='mobile-nav'>
                <div className='nav-items'>
                    <span>
                        <GiHamburgerMenu 
                            size={35}
                            onClick={() => setShowMenu(!showMenu)} //toggles menu open and closed
                        />
                    </span>
                    {menu}
                </div>
            </div>
          </Wrapper>
    )
}


