import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { useParams } from "react-router-dom";
import breakpoint from '../breakpoint';
import Footer from './Footer';


const Shop = ({data, addToCart}) => {
    console.log('dataaa', data)
    const params = useParams();
    console.log('params', params);

    const shoe = data.find(item => `${item.id}` === params.id);
    console.log("shoes from shop:", shoe)

    return (
        <div>
            <Wrapper > 
            <h3>All Shoes ({data.length})</h3>
            <div style={{display: 'flex'}}>

                    <ul className='shoe-type desktop-filter'>
                            <li>Lifestyle</li>
                            <li>Jordan</li>
                            <li>Running</li>
                            <li>Basketball</li>
                            <li>Football</li>
                            <li>Soccer</li>
                            <li>Training + Gym</li>
                            <li>Skateboarding</li>
                            <li>Baseball</li>
                            <li>Golf</li>
                            <li>Tennis</li>
                            <li>Track + Field</li>
                            <li>Walking</li>
                            <li>Shoes $100 + Under</li>
                    </ul>


                <section>

                    {data.map(item => {
                        return(
                        <div className='shoe-info-wrapper' key={item.id}>
                            <div className='shoe-info-sub-wrapper'>
                                <Link to={`/shoe/${item.id}`}><img src={item.image} alt={item.name}/></Link>
                                <div className='descriptions'>
                                    <p className='bold-desc'>{item.name}</p>
                                    <p className='thin-desc'>{item.subTitle}</p>
                                    <p className='thin-desc'>{item.availability}</p>
                                    <p className='bold-desc'>${item.price}</p>
                                    <br/>
                                    <button onClick={() => addToCart(item, 1)}>Add to Cart</button>
                                </div>
                            </div>
                        </div>)
                    })}

                {/* {data.map((item, index) => (
                    <div className='shoe-info-wrapper' key={index}>
                        <div key={index} className='product-item'>
                        <Link to={`/shoe/${item.id}`}><img src={item.image} alt={item.name}/></Link>
                        <h5>{item.name}</h5>
                        <p>${item.price}</p>
                        <br/>
                        <button onClick={() => addToCart(item, 1)}>Add to Cart</button>
                        </div>
                    </div>
                ))} */}

                </section>
                </div>

            <Footer/>
        </Wrapper>

        </div>
    )
}

export default Shop;

const Wrapper = styled.div `
    margin: '0 3vw';

    h3 {
        text-align: left;
        padding-left: 15px;
    }

    .desktop-filter {
        display: none;
    }

    section {
        display: flex;
        flex-wrap: wrap;
    }
    .shoe-info-wrapper {
        justify-content: space-between;
        width: 50vw;
    }
    .shoe-info-sub-wrapper{
        padding: 0 3px 20px 3px;
        text-align: center;
    }
    img {
        width: 100%;
    }

    .descriptions {
        text-align: left;
        padding-left: 10px;
    }
    .bold-desc {
        margin: 0;
        padding-top: 5px; 
        font-weight: 700;
    }
    .thin-desc {
        margin: 0;
        color: #767676;
    }


    @media only screen and ${breakpoint.device.desktop} {
        margin: '0 23px';

        h3 {
            font-size: 25px;
            margin-left: 27px;
        }

        section {
            width: 100%;
        }

        .shoe-info-wrapper {
            width: 24%;
            overflow-x: auto;
        }

        .desktop-filter {
            display: block;
        }

        .shoe-info-sub-wrapper{
            padding: 0 3px 20px 10px;
            text-align: center;
        }

        .shoe-type {
            text-align: left;
            height: 100vh;
            width: 300px;
            list-style: none;
        }
        ul li {
            list-items: none;
            color: #000;
            text-transform: capitalize;
            font-weight: 600;
            padding-bottom: 13px;
        }

        ul li:hover {
            color: gray;
            cursor: pointer;
        }
    }
`;
