import React from 'react';
import styled from 'styled-components';
import breakpoint from '../breakpoints';
import { Link} from 'react-router-dom';
import Footer from './Footer';

const AllShoes = (props) => {
    // console.log("items", props);

    return (
        <Wrapper > 
            <h3>All Shoes ({props.shoeList.length})</h3>
            <section>
                <div style={{}}>
                    <h1>soemnrglkfn</h1>
                </div>
            {props.shoeList.map(item => {
                return(
                <div className='shoe-info-wrapper' key={item.id}>
                    <div className='shoe-info-sub-wrapper'>
                        <Link to={`/shoe/${item.id}`}><img src={item.image} alt={item.name}/></Link>
                        <div className='descriptions'>
                            <p className='bold-desc'>{item.name}</p>
                            <p className='thin-desc'>{item.subTitle}</p>
                            <p className='thin-desc'>{item.availability}</p>
                            <p className='bold-desc'>${item.price}</p>
                        </div>
                    </div>
                </div>)
            })}
            </section>
            <Footer/>
        </Wrapper>
    )
}

export default AllShoes;

const Wrapper = styled.div `
    margin: '0 3vw';

    h3 {
        text-align: left;
        padding-left: 15px;
    }

    section {
        display: flex;
        flex-wrap: wrap;
    }
    .shoe-info-wrapper {
        justify-content: space-between;
        width: 50vw;
    }
    .shoe-info-sub-wrapper{
        padding: 0 3px 20px 3px;
        text-align: center;
    }
    img {
        width: 100%;
    }

    .descriptions {
        text-align: left;
        padding-left: 10px;
    }
    .bold-desc {
        margin: 0;
        padding-top: 5px; 
        font-weight: 700;
    }
    .thin-desc {
        margin: 0;
        color: #767676;
    }

    @media only screen and ${breakpoint.device.desktop} {
        color: red;
        .shoe-info-wrapper{
            width: 300px;
        }

        img {
            // width: 100%;
        }
    }
`;
