import React, { useState } from 'react';
import { useParams, useRouteMatch, Route, NavLink } from "react-router-dom";


const Recipes = (props) => {

    const match = useRouteMatch()
    console.log("match", match)

    const params = useParams();
    const { path, url } = useRouteMatch()

    const recipes = props.recipe.find(item => item.id === params.label);
    console.log(recipes)

    return (
        <div key={props.name}>
            <img src={props.image} alt={props.name}/>
            <p>{props.name}</p>
            {/* <p>{props.name.length < 20 ? `${props.name}` : `${props.name.substring(0, 25)}...`}</p> */}

            {/* our recipe :id is  just the api id..or whatever key we used */}
            <NavLink to={`/recipe/${url}`}><button>{props.view}</button></NavLink>
        </div>
    )
};

export default Recipes;
