import React from "react";
import styled from "styled-components";
import breakpoint from '../breakpoints';
import { Link, useParams } from "react-router-dom";
import Nav from "../components/nav";
import Footer from "../components/footer";

const WomenShoes = ({data ,addToCart}) => {

    document.title = `LacedUp | Women's Shoes`;

    //console.log('dataaa', data)
    const params = useParams();
    console.log('params', params);


    const womenData = data.filter(item => (
        item.category === `Women's Shoes`
    ));
//     console.log('womens data: ', womenData)

    const shoe = data.find(item => `${item.id}` === params.id);
    console.log('shoe',shoe)

    return (
        <section>
            <div className="m-0 px-5 box-border w-full" style={{ maxWidth: '1800px', margin: '0 auto', overflowX: 'hidden'}}>
                <Nav/>
                <Wrapper > 
                <h3 className="m-0">Women's Shoes ({womenData.length})</h3>
                <div style={{display: 'flex'}}>

                        <ul className='shoe-type desktop-filter'>
                                <li>Lifestyle</li>
                                <li>Jordan</li>
                                <li>Running</li>
                                <li>Basketball</li>
                                <li>Football</li>
                                <li>Soccer</li>
                                <li>Training + Gym</li>
                                <li>Skateboarding</li>
                                <li>Baseball</li>
                                <li>Golf</li>
                                <li>Tennis</li>
                                <li>Track + Field</li>
                                <li>Walking</li>
                                <li>Shoes $100 + Under</li>
                        </ul>


                    <section>

                        {womenData.map(item => (

                            <div className='shoe-info-wrapper' key={item.id}>
                                <div className='shoe-info-sub-wrapper'>
                                    <Link to={`/shoe/${item.id}`}><img src={item.image} alt={item.name}/></Link>
                                    <div className='descriptions'>
                                        <p className='bold-desc'>{item.name}</p>
                                        <p className='thin-desc'>{item.category}</p>
                                        <p className='thin-desc'>{item.availability}</p>
                                        <p className='bold-desc'>${item.price}</p>
                                        <br/>
                                        <button onClick={() => addToCart(item, 1)}>Add to Cart</button> 
                                    </div>
                                </div>
                            </div>
                        ))}

                    </section>
                    </div>


            </Wrapper>

            </div>
        <Footer/>
        </section>
    )
}

export default WomenShoes;


const Wrapper = styled.div `
    padding: '0 3vw';

    .desktop-filter {
        display: none;
    }
    section {
        display: flex;
        flex-wrap: wrap;
    }
    .shoe-info-wrapper {
        justify-content: space-between;
        width: 50vw;
    }
    .shoe-info-sub-wrapper{
        padding: 0 3px 20px 3px;
        text-align: center;
    }
    img {
        width: 100%;
    }
    .descriptions {
        text-align: left;
        padding-left: 10px;
    }
    .bold-desc {
        margin: 0;
        padding-top: 5px; 
        font-weight: 700;
    }
    .thin-desc {
        margin: 0;
        color: #767676;
    }
    @media only screen and ${breakpoint.device.xl} {
        margin: '0 23px';

        section {
            width: 100%;
        }
        .shoe-info-wrapper {
            width: 24%;
            overflow-x: auto;
        }
        .desktop-filter {
            display: block;
        }
        .shoe-info-sub-wrapper{
            padding: 0 3px 20px 10px;
            text-align: center;
        }
        .shoe-type {
            text-align: left;
            height: 100vh;
            width: 300px;
            list-style: none;
        }
        ul li {
            list-items: none;
            //color: #000;
            text-transform: capitalize;
            //font-weight: 600;
            padding-bottom: 13px;
        }
        ul li:hover {
            color: gray;
            cursor: pointer;
        }
    }
`;
