import React, { useState, useEffect } from "react";
import {Switch, Link, Route} from 'react-router-dom';
import Cart from './components/Cart';
import Home from './components/Home';
import Shop from './components/Shop';
import './App.css';

const App = () => {
  const [cart, setCart] = useState([]);


  const data = [
    {
      id: 1,
      name: 'Blanket',
      cost: 19.99,
      image: 'https://www.burtsbeesbaby.com/media/catalog/product/H/M/HM10323_Heather%20Grey_alt1.jpg?quality=80&bg-color=255,255,255&fit=bounds&height=90&width=90&canvas=90:90'
    },
    {
      id: 2,
      name: 'Stuffed Bear',
      cost: 10.75,
      image: 'https://i5.walmartimages.com/asr/f10661d8-1568-442a-9a52-36f8fa085084.4d90194419636204495c955bb374e632.jpeg'
    },
    {
      id: 3,
      name: 'Highchair',
      cost: 49.99,
      qty: 0,
      image: 'https://assets.costway.com/media/catalog/product/cache/1/image/530x/9df78eab33525d08d6e5fb8d27136e95/1/_/1_359_58.jpg'
    },
    {
      id: 4,
      name: 'Tommeee Tippee Bottles',
      cost: 15.99,
      image: 'https://www.tommeetippee.com/media/catalog/product/cache/c7c41df5b7c461ca9a3441e2050db103/p/n/png_closer_to_nature_baby_bottle_3_x_9oz_product_only_1.jpg'
    },
    {
      id: 5,
      name: 'Huggies Little Snugglers',
      cost: 32.95,
      image: 'https://images.costcobusinessdelivery.com/ImageDelivery/imageService?profileId=12028466&itemId=955486&recipeName=680'
    },
    {
      id: 6,
      name: 'cutiePat Pacifier',
      cost: 11.99,
      image: 'https://target.scene7.com/is/image/Target/GUEST_aedd2707-2ef0-4ff5-bb63-c5d6acde59c4?fmt=webp&wid=1400&qlt=80'
    } 
  ]


  //adds users selected item
  const addToCart = (product, qty) => {

    setCart([...cart, product]);
    console.log(`${product} has been added to cart.`)    
  }
  console.log('Selected Items:', cart)

  //totals up item costs
  const totalPrice = cart.reduce((prev, curr) => {
    return prev + curr.cost
  }, 0);
  console.log('total price: $',totalPrice)



  return (

      <div className='App'>
       <header>
         <nav>
           <Link to='/' className='nav-item' style={{textDecoration: 'none'}}>Home</Link>
           <Link to='/shop' className='nav-item' style={{textDecoration: 'none'}}>Shop</Link>
           <Link to='/cart' className='nav-item' style={{textDecoration: 'none'}}>Cart<span className='notification'>{cart.length}</span></Link>
         </nav>
       </header>

        <Switch>
          <Route exact path='/' component={Home}/>
          <Route exact path='/shop'>
            <Shop data={data} addToCart={addToCart}/>
          </Route>
            <Route exact path='/cart'>
              <Cart length={cart.length} cartItems={cart} totalPrice={totalPrice}/>
            </Route>
        </Switch>
      </div>


  );
};

export default App;
