import React, { useState, useEffect } from 'react';
import { useParams, Link } from "react-router-dom";

const Recipe = ({ food }) => {
   console.log('FOOD:',food)
    // const data = food.hits;
    const params = useParams();
    console.log(params)
    const recipes = food.find(item => item.recipe.label === params.dish)
    console.log('RECIPES',recipes)
    return (
        <div>
            <Link>Home</Link>
            <h1>{recipes.recipe.label}</h1>
            <img src={recipes.recipe.image} alt={recipes.recipe.label}/>
            <p> Total Calories: {recipes.recipe.calories}</p>
            <p>Total time: {recipes.recipe.totalTime} minutes</p>
        </div>
    )
};

export default Recipe;
