import React from "react";
import styled from "styled-components";
import breakpoint from '../breakpoints';
import { Link, useParams } from "react-router-dom";
import Nav from "../components/nav";
import Footer from "../components/footer";

const MenShoes = ({ data, addToCart }) => {

    document.title = `LacedUp | Men's Shoes`;

    console.log('dataaa', data)
    const params = useParams();
    console.log('params', params);


    const menData = data.filter(item => (
            item.category === `Men's Shoe`
    ));
    //console.log('womens data: ', womenData)

    const shoe = data.find(item => `${item.id}` === params.id);
    console.log('shoe',shoe)

    return (
        <div className="w-full">
            <Nav/>
            <div className="mt-8 md:mt-20">
                <h2 className="mb-4 text-lg md:text-2xl font-medium">Men's Shoes ({menData.length})</h2>
                    <div className="space-x-3 scroll-wrapper flex">
                    <ul className='shoe-type desktop-filter'>
                                <li>Lifestyle</li>
                                <li>Jordan</li>
                                <li>Running</li>
                                <li>Basketball</li>
                                <li>Football</li>
                                <li>Soccer</li>
                                <li>Training + Gym</li>
                                <li>Skateboarding</li>
                                <li>Baseball</li>
                                <li>Golf</li>
                                <li>Tennis</li>
                                <li>Track + Field</li>
                                <li>Walking</li>
                                <li>Shoes $100 + Under</li>
                        </ul>
                        <section>
                            {womenData.map(item => {
                                return (
                                    <Link to={`/shoe/${item.id}`} className="card">
                                        <div key={item.id}>
                                            <img src={item.image} alt={item.name}/>
                                                <div className="flex justify-between text-left">
                                                    <div className="text-md">
                                                        <p className="m-0 pt-2 font-semibold">{item.name}</p>
                                                        <p className="m-0 font-thin" style={{ color: '#767676' }}>{item.category}</p>
                                                    </div>

                                                    <p className="m-0 pt-2 pr-4 text-sm font-semibold">${item.price}</p>
                                                </div>
                                        </div>
                                    </Link>
                                )
                            })}
                        </section>
                    </div>
                </div>
            <Footer/>
        </div>
    )
};

export default MenShoes;
