import React from 'react';
//import {useParams} from 'react-router-dom';
import EmptyCart from './EmptyCart';
import styled from 'styled-components';
//import { Link } from 'react-router-dom';
import breakpoint from '../../breakpoint';
import Footer from './Footer'

const Cart = ({length, cartItems, totalPrice}) => {

    //console.log('cart items', cartItems)
    //const params = useParams();
    //console.log('params', params)

    /*
    totals up item costs
    const totalQty = cartItems.reduce((prev, curr) => {
        return prev + curr.qty
    }, 0);
    console.log('total qty:',totalQty)
    */
   document.title = `LacedUp | My Cart (${cartItems.length})`;

    const tax = totalPrice * 0.07;

    //format into US currency
    function formatMoney(number) {
        return number.toLocaleString('en-US', { style: 'currency', currency: 'USD' });
    }

    const removeItem = (e) => {
        cartItems.filter((item) => (
            item.id
        ))
    }

    const editItem = () => {
        //
    }

    return (
        <div>
            <p>
                {cartItems.length === 1 ? `You have ${cartItems.length} item in your cart` : 
                (cartItems.length > 1 ? `You have ${cartItems.length} items in your cart`: 
                <EmptyCart/>)}
            </p>

            <Wrapper>
                <div className='product-list'>


                {cartItems.map((item, index) => (
                    <div key={index}>
                        {item.product.length === 1 ? 
                        <div>
                            <div style={{display: 'flex'}}>
                                <img src={item.product.image} alt={item.product.name} style={{width: '150px'}}/>
                                <div>
                                    <h3>{item.product.name}</h3>
                                    <p>${item.product.price}</p>
                                    <h5>Qty: {item.qty}</h5> 
                                </div>
                            </div> 
                        </div>
                        : 
                        <div style={{display: 'flex'}}>
                            <div style={{justifyContent: 'space-between', margin: '15px 40px', borderTop: '1px solid #404040'}}>
                                <div style={{width: '50%'}}>
                                    <img src={item.product.image} alt={item.product.name} style={{width: '150px'}}/>
                                    <div>
                                        <h3>{item.product.name}</h3>
                                        <h5>Qty: {item.qty}</h5> 
                                        <p onClick={removeItem}>Remove</p>
                                    </div>
                                    <p>${item.product.price}</p>
                                </div>
                            </div>
                            {/* cart */}
                            <div>
                                <div className='product-checkout'>
                                    <h4>Summary</h4>
                                    <div style={{display: 'flex', justifyContent: 'space-between'}}>
                                        <p>Subtotal</p>
                                        <p>{formatMoney(totalPrice)}</p>
                                    </div>
                                    <div style={{display: 'flex', justifyContent: 'space-between'}}>
                                        <p>Estimated Shipping & Handling</p>
                                        <p>$0.00</p>
                                    </div>
                                    <div style={{display: 'flex', justifyContent: 'space-between'}}>
                                        <p>Estimated Tax</p>
                                        <p>{formatMoney(tax)}</p>
                                    </div>

                                    <p style={{backgroundColor: '#131921', color: 'white', padding: '10px 0'}}>Total: {formatMoney(totalPrice + tax)}</p>
                                    <div>
                                        <button>Checkout</button>
                                    </div>
                                </div>
                            </div>
                        </div>

                        }

                    </div>
                ))}

                </div>

            </Wrapper>
            <Footer/>
        </div>
    )
}

export default Cart;


const Wrapper = styled.div`
padding-bottom: 130px;

.product-checkout {
    margin: 0 20px;
}

@media only screen and ${breakpoint.device.desktop} {


    display: flex;


    .product-list {
        //width: 60%;
    }

    .product-checkout {
        //width: 40%;
        margin-right: 40px;
    }
}
`
