import React, { useState } from 'react';
import { Link } from "react-router-dom";

const Recipes = (props) => {

    //console.log(typeof(props.name))
    const [data, setData] = useState()
    return (
        <div key={props.name}>
            <img src={props.image} alt={props.name}/>
            <p>{props.name}</p>
            {/* <p>{props.name.length < 20 ? `${props.name}` : `${props.name.substring(0, 25)}...`}</p> */}

            {/* our recipe :id is  just the api id..or whatever key we used */}
            <Link to={
                `/recipe/${props.name}`
            }><button>{props.view}</button></Link>
        </div>
    )
};

export default Recipes;
