import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Link, Switch } from "react-router-dom";

import AvengersList from "./components/AvengersList";
import Avenger from './components/Avenger';
import Home from './components/Home';

const App = () => {

  const api = 'https://jsonplaceholder.typicode.com/users';
    const [data, setData] = useState([]);
    const id = 'hi'
    useEffect(() => {
        axios.get(`${api}`)
            .then((res) => {
                console.log(res.data);
                setData(res.data)
            })
            .catch((err) => {
                console.log(err)
            })
    }, [])

  return (
    <Router>
      <nav className="navbar">
        <li>
          <Link to="/">Home</Link>
        </li>
        <li>
          <Link to="/avengers">Avengers</Link>
        </li>
      </nav>
      <div className="App">
        <Switch>
          <Route exact path="/" component={Home} />

          <Route path="/avengers/">
            <AvengersList hero={hero}/>
          </Route>

          {/* we can use Route to render child components instead of having to use the component prop. 
          This way we can easily pass down props to our components. Our routes with dynamic data will now look like this */}
          <Route path='/avengers/:hero'>
            {/* Passing our data to avenger's list */}
            <Avenger key={hero.id} hero={hero}/>
          </Route>

        </Switch>
      </div>
    </Router>
  );
};

export default App;
