                </div>
                <p style={{margin: '0', fontWeight: '600'}}>${shoe.price}</p>
            </div>

            <img src={shoe.image} alt={shoe.name} style={{width: '100%'}}/>
           <ShoeSize/>
           <div>
               <p></p>
           </div>
        </div>
    )
}

export default Shoe;
