import React from "react";
import styled from "styled-components";
import breakpoint from '../breakpoints';
import { Link, useParams } from "react-router-dom";
import Nav from "../components/nav";
import Footer from "../components/footer";

//button class
import { Button } from "../config/Button";

const WomenShoes = ({ data, addToCart }) => {

    document.title = `LacedUp | Women's Shoes`;

    console.log('dataaa', data)
    const params = useParams();
    console.log('params', params);


    const womenData = data.filter(item => (
            item.category === `Women's Shoe`
    ));
    console.log('womens data: ', womenData)

    const shoe = data.find(item => `${item.id}` === params.id);
    console.log('shoe',shoe)

    let blackBtn = new Button(
        border= 'none'
    )

    return (
        <div className="w-full">
            <Nav/>
            <Wrapper className="mt-8 md:mt-20">
                <h2 className="mb-4 text-lg md:text-2xl font-medium">Women's Shoes ({womenData.length})</h2>
                    <div className="flex">
                        <ul className='shoe-type desktop-filter'>
                            <li>Lifestyle</li>
                            <li>Jordan</li>
                            <li>Running</li>
                            <li>Basketball</li>
                            <li>Football</li>
                            <li>Soccer</li>
                            <li>Training + Gym</li>
                            <li>Skateboarding</li>
                            <li>Baseball</li>
                            <li>Golf</li>
                            <li>Tennis</li>
                            <li>Track + Field</li>
                            <li>Walking</li>
                            <li>Shoes $100 + Under</li>
                        </ul>
                     <section>
                        {womenData.map(item => (
                            <Link to={`/shoe/${item.id}`} className="card">
                                <div key={item.id}>
                                        <img src={item.image} alt={item.name}/>
                                            <div className="flex justify-between text-left">
                                                <div className="text-md">
                                                    <p className="m-0 pt-2 font-semibold">{item.name}</p>
                                                    <p className="m-0 font-thin" style={{ color: '#767676' }}>{item.category}</p>
                                                </div>

                                                <p className="m-0 pt-2 pr-4 text-sm font-semibold">${item.price}</p>
                                                <br/>
                                                <button onClick={() => addToCart(item, 1)}>Add to Cart</button> 
                                            </div>
                                </div>
                            </Link>
                        ))}
                    </section>
                </div>
            </Wrapper>
            <Footer/>
        </div>
    )
};

export default WomenShoes;

const Wrapper = styled.div `
    padding: '0 3vw';
    max-width: 1920px;
    margin: 0 auto;
    h3 {
        text-align: left;
        padding-left: 15px;
    }
    .desktop-filter {
        display: none;
    }
    section {
        display: flex;
        flex-wrap: wrap;
    }
    .shoe-info-wrapper {
        justify-content: space-between;
        width: 50vw;
    }
    .shoe-info-sub-wrapper{
        padding: 0 3px 20px 3px;
        text-align: center;
    }
    img {
        width: 100%;
    }
    .descriptions {
        text-align: left;
        padding-left: 10px;
    }
    .bold-desc {
        margin: 0;
        padding-top: 5px; 
        font-weight: 700;
    }
    .thin-desc {
        margin: 0;
        color: #767676;
    }
    @media only screen and ${breakpoint.device.xxl} {
        margin: '0 23px';
        h3 {
            font-size: 25px;
            margin-left: 27px;
        }
        section {
            width: 100%;
        }
        .shoe-info-wrapper {
            width: 24%;
            overflow-x: auto;
        }
        .desktop-filter {
            display: block;
        }
        .shoe-info-sub-wrapper{
            padding: 0 3px 20px 10px;
            text-align: center;
        }
        .shoe-type {
            text-align: left;
            height: 100vh;
            width: 300px;
            list-style: none;
        }
        ul li {
            list-items: none;
            //color: #000;
            text-transform: capitalize;
            //font-weight: 600;
            padding-bottom: 13px;
        }
        ul li:hover {
            color: gray;
            cursor: pointer;
        }
    }
`;
